mapper.select:

where menu_type_id=#{menuTypeId}

	AND menu_name like concat('%', #{menuName}, '%')


	AND status = #{status}

order by parent_id, menu_order

select distinct m.id, m.parent_id, m.menu_name, m.path, m.component, m.status, m.menu_type_id, m.icon, m.menu_order, m.create_time from nt_sys_menu m left join nt_sys_role_menu rm on m.id = rm.menu_id left join nt_sys_user_role ur on rm.role_id = ur.role_id left join nt_sys_role ro on ur.role_id = ro.id where ur.user_id = #{userId} and m.menu_type_id=#{menuTypeId}
AND menu_name like concat('%', #{menuName}, '%')
AND status = #{status}
order by m.parent_id, m.menu_order

select DISTINCT b.id, b.menu_name, b.icon, b.menu_order, b.component, b.path,
	b.menu_description, b.another_name, b.parent_id
from nt_sys_role_menu a, nt_sys_menu b 
where a.menu_id=b.id and b.status='1'
	
		and a.role_id in 
		
			#{id}
		
	
	
	 	and b.parent_id = #{parentId} 
	
	
		and a.menu_type_id = #{menuTypeId}
	
order by b.menu_order
